import React, { ChangeEvent, useEffect, useRef } from 'react';
import styles from "./styles.module.scss";
import arrIcon from "../SignInEmail/right.png";

function SignInEmailCode() {
    const useInputRefs = (length: number) => {
        return Array.from({ length }, () => useRef<HTMLInputElement | null>(null));
    };

    const inputRefs = useInputRefs(6);

    const handleInputChange = (index: number, e: ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value;
        if (value.length === 1 && index < inputRefs.length - 1 && inputRefs[index + 1]?.current) {
            inputRefs[index + 1].current.focus();
        }
    };

    useEffect(() => {
        const handlers = inputRefs.map((ref, index) => {
            const handleChange = (e: ChangeEvent<HTMLInputElement>) => handleInputChange(index, e);
            if (ref.current) {
                ref.current.addEventListener('input', handleChange);
            }
            return () => {
                if (ref.current) {
                    ref.current.removeEventListener('input', handleChange);
                }
            };
        });

        return () => {
            handlers.forEach(removeHandler => removeHandler());
        };
    }, [inputRefs]);

    return (
        <div className={styles.container}>
            <form className={styles.form}>
                <h2 className={styles.tytul}>Verification</h2>
                <div className={styles.inputContainer}>
                    {inputRefs.map((ref, index) => (
                        <input
                            key={index}
                            type="text"
                            className={styles.numberInput}
                            placeholder="-"
                            maxLength={1}
                            ref={ref}
                        />
                    ))}
                </div>
                <div className={styles.submitContainer}>
                    <input type="submit" value="CONTINUE" className={styles.submit} />
                    <img src={arrIcon} className={styles.img2} alt="Arr Icon" />
                </div>
            </form>
        </div>
    );
}

export default SignInEmailCode;
