import React, { ChangeEvent, useEffect, useRef } from 'react';
import styles from "./styles.module.scss";
import arrIcon from "../SignInEmail/right.png";

function SignInEmailCode() {
    const inputRefs = Array.from({ length: 6 }, () => useRef<HTMLInputElement | null>(null));

    useEffect(() => {
        const handleInputChange = (index: number, e: ChangeEvent<HTMLInputElement>) => {
            const value = e.target.value;
            if (value.length === 1 && index < inputRefs.length - 1) {
                inputRefs[index + 1].current?.focus();
            }
        };

        inputRefs.forEach((ref, index) => {
            if (ref.current) {
                ref.current.addEventListener('input', (e) => handleInputChange(index, e as unknown as ChangeEvent<HTMLInputElement>));
            }
        });

        return () => {
            inputRefs.forEach((ref, index) => {
                if (ref.current) {
                    ref.current.removeEventListener('input', (e) => handleInputChange(index, e as unknown as ChangeEvent<HTMLInputElement>));
                }
            });
        };
    }, [inputRefs]);

    return (
        <div className={styles.container}>
            <form className={styles.form}>
                <h2 className={styles.tytul}>Verification</h2>
                <div className={styles.inputContainer}>
                    {inputRefs.map((ref, index) => (
                        <input
                            key={index}
                            type="text"
                            className={styles.numberInput}
                            placeholder="-"
                            maxLength={1}
                            ref={ref}
                        />
                    ))}
                </div>
                <div className={styles.submitContainer}>
                    <input type="submit" value="CONTINUE" className={styles.submit} />
                    <img src={arrIcon} className={styles.img2} alt="Arr Icon" />
                </div>
            </form>
        </div>
    );
}

export default SignInEmailCode;
